import React, { Component } from 'react';
import { GlobalStoreContext } from '../store'

export default class DeleteListModal extends Component {
    constructor(props) {
        super(props);
        //looks weird because i called the object song and it has a song
        this.state = {
            title: null,
            artist: null,
            youTubeId: null
        }            
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        //if previous song is different from the current, we are on a new song
        if(prevProps.idNamePair.name !== this.props.idNamePair.name){
            let song = this.props.song
            this.setState({
                title : song.title,
                artist: song.artist,
                youTubeId: song.youTubeId
            })
        }
    }
    render() {
        const { idNamePair, deleteListCallback, hideDeleteListModalCallback } = this.props;
        let name = "";
        if (idNamePair) {
            name = idNamePair.name;
        }
        return (
            <div 
                class="modal" 
                id="delete-list-modal" 
                data-animation="slideInOutLeft">
                    <div class="modal-root" id='verify-delete-list-root'>
                        <div class="modal-north">
                            Delete playlist?
                        </div>
                        <div class="modal-center">
                            <div class="modal-center-content">
                                Are you sure you wish to permanently delete the {name} playlist?
                            </div>
                        </div>
                        <div class="modal-south">
                            <input type="button" 
                                id="delete-list-confirm-button" 
                                class="modal-button" 
                                onClick={deleteListCallback}
                                value='Confirm' />
                            <input type="button" 
                                id="delete-list-cancel-button" 
                                class="modal-button" 
                                onClick={hideDeleteListModalCallback}
                                value='Cancel' />
                        </div>
                    </div>
            </div>
        );
    }
}
